INSERT INTO `news` (`id`, `title`, `description`, `image`, `create_at`, `update_at`) VALUES
(2, 'Авиакомпания меняет багажную политику в худшую сторону', 'Turkish Airlines сократит бесплатную норму багажа для пассажиров эконом-класса на большинстве украинских маршрутов, рейсы по которым будут выполняться с 1 июля, сообщает прес-служба авиакомпании.\r\n\r\nСвязано это с переводом всех международных направлений, в том числе маршрутов между Киевом, Львовом, Одессой, Запорожьем, Херсоном, Харьковом и Стамбулом с весовой концепции на штучную концепцию перевозки багажа.\r\n\r\nНовая багажная политика позволит пассажирам эконом-класса перевозить бесплатно один чемодан весом до 23 кг, а пассажирам бизнес-класса - два чемодана весом до 32 кг каждый на рейсах из Украину в Стамбул.\r\n\r\nСейчас пассажиры и эконом, и бизнес-класса на маршрутах Львов-Стамбул и Одесса-Стамбул могут бесплатно сдавать в багаж 40 кг вещей без ограничения по числу предметов.\r\n\r\nБагажная норма на маршрутах Запорожье-Стамбул и Херсон-Стамбул предусматривает бесплатную перевозку до 30 кг вещей для пассажиров обоих классов без ограничения по числу предметов.\r\n\r\nНа линиях Киев-Стамбул и Харьков-Стамбул действующая багажная норма позволяет перевозить бесплатно 20 кг багажа пассажирам эконом-класса и 30 кг пассажирам бизнес-класса.', '', NULL, NULL),
(4, 'Сколько украинцев смогут поехать в Европу без визы', 'На стартовавшей в Киеве ежегодной пресс-конференции Петра Порошенко президент поздравил украинцев с получением безвизового режима с ЕС.\r\n\r\nТакже президент озвучил цифры по загранпаспортам в стране. Из озвученной информации следует, что более трех миллионов граждан Украины уже в июне получат возможность безвизового посещения стран Евросоюза - все кто оформил биометрические паспорта.\r\n\r\n"Более 3 млн 300 тысяч украинских граждан уже получили биометрические паспорта в ожидании исторического решения. Еще миллионы имеют на руках обычные загранпаспорта с ранее выданными визами", - заявил Порошенко.\r\n\r\nТакже он заверил, что все ранее получившие визы могут ездить с ними в страны ЕС без проблем.\r\n\r\n"В 2016 году более 15 млн раз украинцы перескали границу с Европейским союзом, и, теперь, надеюсь, исчезнут очереди в посольствах, консульствах и визовых центрах. Останется в прошлом бумажная волокита", - добавил президент.\r\n\r\nПорошенко напомнил, что украинцы сейчас могут ездить без виз в 52 страны мира, а в июне добавятся 34 страны ЕС. А это еще добавит либерализацию виз около 20 стран из "белого шенгенского списка" -  Европы, Азии и Южной Америки.\r\n\r\nТакже, по словам Порошенко, 144 пункта реформ позволили получить Украине безвиз.\r\n\r\nНапомним, ранее Исландия пообещала безвиз Украине после ЕС.', '', NULL, NULL),
(5, 'Подробности о новых лоу-кост тарифах МАУ', 'Принцип бронирования\r\n\r\nКлассические авиакомпании применяют принцип ценообразования, согласно которому два отдельно купленных билета туда и обратно обычно стоят дороже по сравнению с единым билетом туда-обратно.\r\n\r\nЦенообразование по тарифу лоу-кост МАУ строит из расчета полетов в одну сторону. Это означает, что если покупать билеты туда и обратно отдельно, они будут стоить как единый билет туда-обратно.\r\n\r\n \r\n\r\nОграничения\r\n\r\nТариф лоу-кост не распространяется на перелеты со стыковкой. Если Вы хотите лететь, например, из Одессы через Киев в Амстердам, вам придется купить два отдельных билета: Одесса-Киев и Киев-Амстердам.\r\n\r\nЛоу-кост билеты МАУ не подлежат обмену, возврату и другим изменениям даже с уплатой штрафа. Всего в год авиакомпания планирует продавать около 500 тыс. билетов по тарифу лоу-кост.\r\n\r\nПровозить бесплатно можно только один предмет ручной клади с габаритами до 55х40х20 см и весом до 7 кг и личную вещь, например, дамскую сумочку или ноутбук. Для владельцев Panorama Club Classic/Premium действует увеличенная норма ручной клади - 12 кг вместо 7 кг. Багаж можно докупить отдельно по стандартным тарифам.\r\n\r\nПитание по тарифу лоу-кост не предоставляется. В полете бесплатно пассажирам предоставляется только вода. На международных рейсах питание и напитки можно купить на борту или же заказать заранее за плату.\r\n\r\n \r\n\r\nPanorama Club\r\n\r\nПассажиры по билетам лоу-кост не смогут зарабатывать мили в программе лояльности МАУ Panorama Club. Также не будут засчитываться полетные сегменты, которые необходимы для повышения уровня статуса карты.\r\n\r\nТариф лоу-кост не предусматривает возможность использования миль для повышения класса обслуживания с экономического на бизнес или же для получения скидки на покупку билета.\r\n\r\n \r\n\r\nБесплатные опции\r\n\r\nНа данный момент пассажиры по тарифу лоу-кост смогут регистрироваться на рейсы МАУ в аэропорту бесплатно. В процессе онлайн-регистрации, которая начинается за 48 часов до вылета, пассажиры могут бесплатно выбрать место в самолете.', '', NULL, NULL);

